// @ts-check
/**
 * Cell in a ExcelRow
 */
export class ExcelCell {
    /**
     * @param {*} value - value to go into the Cell
     * @param {*} [type] - The type of the ExcelCell. Default is 'String'.
     * @param {*} [styleId] - The style id to apply to the Cell see ExcelStyle
     */
    constructor(value, type, styleId) {
        this.styleId = styleId || null;
        this.type = type || 'String';
        this.value = value === undefined ? '' : value;
    }
}
