// @ts-check
/**
 * Row in an Excel table
 */
export class ExcelRow {
    /**
     * @param {import('./excelCell').ExcelCell[]} cells - cells to insert into the row
     */
    constructor(cells) {
        /** @type {import('./excelCell').ExcelCell[]} */
        this.cells = cells || [];
    }

    /**
     * @param {import('./excelCell').ExcelCell} cell - cell to insert into this ExcelRow
     */
    addCell(cell) {
        this.cells.push(cell);
    }

    /**
     * @param {number} colIndex - colIndex of the ExcelCell to find
     * @returns {import('./excelCell').ExcelCell}
     */
    getCell(colIndex) {
        return this.cells[colIndex];
    }
}
