// @ts-check
/**
 * Class for Excel Styles
 * Edit the instance properties to control the styles.
 */
export class ExcelStyle {
    /**
     * @param {string} [id] - defulat is 'Default'
     * @param {string} [name] - default is 'Normal'
     */
    constructor(id, name) {
        this.id = id || 'Default';
        this.name = name || 'Normal';
        this.properties = {
            Alignment: { Vertical: 'Bottom' },
            Borders: {},
            Font: {},
            Interior: {},
            NumberFormat: {},
            Protection: {},
        };
    }
}
