// @ts-check
/**
 * Excel Table that can be added to ExcelWorkSheets
 */
export class ExcelTable {
    /**
     * @param {import('./excelRow').ExcelRow[]} rows - Rows to put into the table
     */
    constructor(rows) {
        /** @type {import('./excelRow').ExcelRow[]} */
        this.rows = rows || [];
    }

    /**
     * @param {number} row
     * @param {number} col
     * @returns {import('./excelCell').ExcelCell}
     */
    getCell(row, col) {
        return this.rows[row].getCell(col);
    }

    /** @returns {number} iterates over all rows in the table and returns the column count of the row with the most columns. */
    getMaxColCount() {
        let maxColCount = 0;
        for (let i = 0; i < this.rows.length; i++)
            if (this.rows[i].cells.length > maxColCount) maxColCount = this.rows[i].cells.length;
        return maxColCount;
    }
}
