// @ts-check
/**
 * Class for Excel Worksheets
 */
export class ExcelWorkSheet {
    /**
     * @param {string} name - name of the worksheet
     * @param {import('./excelTable').ExcelTable[]} [tables] - tabels to insert into the Excel
     */
    constructor(name, tables) {
        this.name = name;
        /** @type {import('./excelTable').ExcelTable[]} */
        this.tables = tables || [];
    }

    /**
     * @param {import('./excelTable').ExcelTable} table - Adds a new Table to the Excel document
     */
    addTable(table) {
        this.tables.push(table);
    }
}
