import { translate as t } from '../lib/i18n.js';
import { CoreBaseButton } from './core-base-button.js';

/** UI button for cancel operations */
class CoreApplyButton extends CoreBaseButton {
    /** @inheritdoc */
    constructor() {
        // Must call superconstructor first.
        super();

        /** @type {import('./core-base-button.js').ButtonType} */
        this.buttonType = 'positive';
        this.buttonTitle = 'core-wc-buttons:apply';
    }

    /** @inheritdoc */
    get buttonEventName() {
        return 'applyAction';
    }
}
customElements.define('core-apply-button', CoreApplyButton);
