import { CoreBaseButton } from './core-base-button.js';

/** UI button for cancel operations */
class CoreCancelButton extends CoreBaseButton {
    /** @inheritdoc */
    constructor() {
        // Must call superconstructor first.
        super();

        /** @type {import('./core-base-button.js').ButtonType} */
        this.buttonType = 'negative';
        this.buttonTitle = 'core-wc-buttons:cancel';
    }

    /** @inheritdoc */
    get buttonEventName() {
        return 'cancelAction';
    }
}
customElements.define('core-cancel-button', CoreCancelButton);
