import { CoreBaseButton } from './core-base-button.js';

/** This class creates a yes button by extending the CoreCustomButton */
class CoreYesButton extends CoreBaseButton {
    /** @inheritdoc */
    constructor() {
        // Must call superconstructor first.
        super();

        /** @type {import('./core-base-button.js').ButtonType} */
        this.buttonType = 'positive';
        this.buttonTitle = 'core-wc-buttons:yes';
    }

    /** @inheritdoc */
    get buttonEventName() {
        return 'yesAction';
    }
}
customElements.define('core-yes-button', CoreYesButton);
