import { i18nInit } from '@trapezegroup/core-js-app/build/es6-bundled/index.js';
import { translate as t } from 'lit-i18n/src/lit-i18n.js';

export const namespace = 'core-wc-buttons';
// @ts-ignore
i18nInit({ importMeta: import.meta, namespace });

/**
 * @param {string} key translation key
 * @param {object} [param] optional param that will be consumed by the translation
 * @returns {object} directive
 */
export function translate(key, param) {
    return t(`${namespace}:${key}`, param || {});
}
