import { CoreBaseButton } from './core-base-button.js';

/** This class creates a no button by extending the CoreCustomButton */
class CoreNoButton extends CoreBaseButton {
    /** @inheritdoc */
    constructor() {
        // Must call superconstructor first.
        super();

        /** @type {import('./core-base-button.js').ButtonType} */
        this.buttonType = 'negative';
        this.buttonTitle = 'NO';
    }

    /** @inheritdoc */
    get buttonEventName() {
        return 'noAction';
    }
}
customElements.define('core-no-button', CoreNoButton);
