import { CoreBaseButton } from './core-base-button.js';

/** This class creates an ok button by extending the CoreCustomButton */
class CoreOkButton extends CoreBaseButton {
    /** @inheritdoc */
    constructor() {
        // Must call superconstructor first.
        super();

        /** @type {import('./core-base-button.js').ButtonType} */
        this.buttonType = 'positive';
        this.buttonTitle = 'OK';
    }

    /** @inheritdoc */
    get buttonEventName() {
        return 'okAction';
    }
}
customElements.define('core-ok-button', CoreOkButton);
