// @ts-check
/**  import litElement base class and html helper function */
import { LitElement, html, css } from 'lit-element';
/** This is an example of a component which extends the litElement */
class CoreModalContent extends LitElement {
    /** @inheritdoc */
    static get styles() {
        return [
            css`
                :host {
                    display: block;
                }
            `,
        ];
    }

    /** We can initialize the properties here in the constructor */
    constructor() {
        // Must call superconstructor first.
        super();
    }

    /** @inheritdoc */
    render() {
        return html`
        <div class="content">
            <slot name="content"></slot>
        </div>
    `;
    }
}
customElements.define('core-modal-content', CoreModalContent);
