// @ts-check
/**  import litElement base class and html helper function */
import { LitElement, html, css } from 'lit-element';

/** This is an example of a component which extends the litElement */
class CoreModalFooter extends LitElement {
    /** @inheritdoc */
    static get styles() {
        return [
            css`
                :host {
                    display: block;
                }
                .actions {
                    display: flex;
                    justify-content: center;
                    flex-wrap: wrap;
                    padding: 0.6rem;
                }

                .actions ::slotted(*) {
                    margin-left: 0.6rem;
                    margin-right: 0.6rem;
                }
            `,
        ];
    }

    /** We can initialize the properties here in the constructor */
    constructor() {
        // Must call superconstructor first.
        super();
    }

    /** @inheritdoc */
    render() {
        return html`
        <div class="actions">
            <slot name="negativeButton"></slot>
            <slot name="intermediateButton"></slot>
            <slot name="positiveButton"></slot>
        </div>
    `;
    }
}
customElements.define('core-modal-footer', CoreModalFooter);
