// @ts-check
/**  import litElement base class and html helper function */
import { LitElement, html, css } from 'lit-element';

/** This is an example of a component which extends the litElement */
class CoreWorkspaceContent extends LitElement {
    /** @inheritdoc */
    static get styles() {
        return [
            css`
                :host {
                    display: block;
                    height: calc(100% - 9.2rem);
                    overflow: auto;
                    width: 100%;
                    margin-top: 5rem;
                }
            `,
        ];
    }

    /** We can initialize the properties here in the constructor */
    constructor() {
        // Must call superconstructor first.
        super();
    }

    /** @inheritdoc */
    render() {
        return html`
            <slot name="content"></slot>
    `;
    }
}
customElements.define('core-workspace-content', CoreWorkspaceContent);
